create table `user` (`id` varchar(36) not null primary key, `name` text not null, `email` varchar(255) not null unique, `emailVerified` boolean not null, `image` text, `createdAt` timestamp default CURRENT_TIMESTAMP not null, `updatedAt` timestamp default CURRENT_TIMESTAMP not null);

create table `session` (`id` varchar(36) not null primary key, `expiresAt` timestamp not null, `token` varchar(255) not null unique, `createdAt` timestamp default CURRENT_TIMESTAMP not null, `updatedAt` timestamp not null, `ipAddress` text, `userAgent` text, `userId` varchar(36) not null references `user` (`id`) on delete cascade);

create table `account` (`id` varchar(36) not null primary key, `accountId` text not null, `providerId` text not null, `userId` varchar(36) not null references `user` (`id`) on delete cascade, `accessToken` text, `refreshToken` text, `idToken` text, `accessTokenExpiresAt` timestamp, `refreshTokenExpiresAt` timestamp, `scope` text, `password` text, `createdAt` timestamp default CURRENT_TIMESTAMP not null, `updatedAt` timestamp not null);

create table `verification` (`id` varchar(36) not null primary key, `identifier` text not null, `value` text not null, `expiresAt` timestamp not null, `createdAt` timestamp default CURRENT_TIMESTAMP not null, `updatedAt` timestamp default CURRENT_TIMESTAMP not null);